var suggestions=document.getElementById('suggestions');var userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(e){if(e.keyCode===191){e.preventDefault();userinput.focus();}
if(e.keyCode===27){userinput.blur();suggestions.classList.add('d-none');}}
document.addEventListener('click',function(event){var isClickInsideElement=suggestions.contains(event.target);if(!isClickInsideElement){suggestions.classList.add('d-none');}});document.addEventListener('keydown',suggestionFocus);function suggestionFocus(e){const focusableSuggestions=suggestions.querySelectorAll('a');const focusable=[...focusableSuggestions];const index=focusable.indexOf(document.activeElement);let nextIndex=0;if(e.keyCode===38){e.preventDefault();nextIndex=index>0?index-1:0;focusableSuggestions[nextIndex].focus();}
else if(e.keyCode===40){e.preventDefault();nextIndex=index+1<focusable.length?index+1:index;focusableSuggestions[nextIndex].focus();}}
(function(){var index=new FlexSearch({preset:'score',cache:true,doc:{id:'id',field:['title','description','content',],store:['href','title','description',],},});var docs=[{id:0,href:"https://bitlogic.github.io/doks-algarrobo/docs/promos/introduction/",title:"Introducción a Promos",description:"Descripción funcional del nuevo sistema de Promos.",content:"\u003ch2 id=\"casos-de-uso\"\u003eCasos de uso\u003c/h2\u003e\n\u003ch3 id=\"gestión-de-promociones\"\u003eGestión de promociones\u003c/h3\u003e\n\u003cp\u003ePara gestionar los promos. \u003ca href=\"/doks-algarrobo/docs/qbit/gestion-enunciados/\"\u003eGestión de Promos →\u003c/a\u003e\u003c/p\u003e\n"},{id:1,href:"https://bitlogic.github.io/doks-algarrobo/docs/qbit/introduction/",title:"Introducción a Qbit",description:"Descripción funcional del nuevo sistema Q.",content:"\u003ch2 id=\"casos-de-uso\"\u003eCasos de uso\u003c/h2\u003e\n\u003ch3 id=\"gestión-de-enunciados\"\u003eGestión de enunciados\u003c/h3\u003e\n\u003cp\u003ePara gestionar los enunciados. \u003ca href=\"/doks-algarrobo/docs/qbit/gestion-enunciados/\"\u003eGCP →\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id=\"gestión-de-tipos-de-exámenes\"\u003eGestión de tipos de exámenes\u003c/h3\u003e\n\u003cp\u003ePara gestionar los tipos de exámenes \u003ca href=\"/doks-algarrobo/docs/qbit/abm-tipo-examen/\"\u003eGTE →\u003c/a\u003e\u003c/p\u003e\n"},{id:2,href:"https://bitlogic.github.io/doks-algarrobo/docs/promos/abm-promos/",title:"Creación de promociones comerciales",description:"Cuando usas la herramienta puedes ver, editar y agregar promos.",content:"\u003ch2 id=\"crear-promociones-comerciales\"\u003eCrear promociones comerciales\u003c/h2\u003e\n\u003cp\u003eCon esta herramienta puedes configurar promociones comerciales que luego se utilizarán en la aplicación de ePagos.\nLas promociones comerciales pueden configurarse para un monto fijo o un porcentaje que luego se aplicará al ticket que el estudiante debe abonar.\u003c/p\u003e\n\u003col\u003e\n\u003cli\u003eIngresa a la herramienta de gestión de ingresos.\u003c/li\u003e\n\u003cli\u003eEn el panel de navegación, selecciona el icono \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/prom.png\" alt=\"\"\u003e para que puedas acceder a la pantalla de creación de las promociones.\u003c/li\u003e\n\u003cli\u003eHaz clic en el botón \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/nueva.png\" alt=\"\"\u003e para comenzar.\u003c/li\u003e\n\u003cli\u003eEn la pantalla, comienza a completar los campos con los datos de la pormoción que quieres crear. Selecciona en primer lugar si va a ser un monto fijo o un porcentaje.\u003c/li\u003e\n\u003cli\u003eLuego, completa el nombre, la descripción, selecciona el tipo de estudiante, el perído y la validez de la promoción.\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"note note-warning\" role=\"note\"\u003e\n  \u003cdiv class=\"flex-shrink-1 note-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0V0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M11 7h2v2h-2zm0 4h2v6h-2zm1-9C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eNota\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eLa definición de nuevo inscripto hace referencia un estudiante que acaba de ingresar a la organización. Mientras que el término reinscripto abarca a aquellos alumnos y alumnas que ya pertenecen a la organización porque han cursado anteriormente.\u003c/div\u003e\n\u003c/div\u003e\n\u003col start=\"6\"\u003e\n\u003cli\u003e\n\u003cp\u003eActive la promoción dislizando el botón de \u0026ldquo;Estado\u0026rdquo; hacia la derecha.Para desactivarlo, deslízalo hacia la izquierda.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eEl la sección \u0026ldquo;Tickets\u0026rdquo; puedes optar por el tipo de arancel y tickets a los que afectará esta promoción.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eDirígete a la pestaña \u0026ldquo;Criterios\u0026rdquo;. Aquí podrás configurar las reglas que se considerarán para hacer efectivo el descuento.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eHaz clic en el icono de \u0026ldquo;Entradas\u0026rdquo; \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/entrada.png\" alt=\"\"\u003e para elegir la población para quiénes aplican estas reglas. Puedes optar entre: Región, Modalidad, SubModalidad, Clase estudio, Carrera, Site, Turno, Marca y Paquete. Todas estas opciones tienen listas desplegables.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eEn las columnas de salida, dentro del campo \u0026ldquo;Valor\u0026rdquo; puedes definir el monto de la promoción o el número de porcentaje.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eSi fuese necesario configurar esta promoción para que se aplique en diversas situaciones, tendrás que seleccionar \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/agregar%20prom%20comercial.png\" alt=\"\"\u003e para sumar las definiciones de cada una con sus criterios.\u003c/p\u003e\n\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"note note-warning\" role=\"note\"\u003e\n  \u003cdiv class=\"flex-shrink-1 note-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0V0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M11 7h2v2h-2zm0 4h2v6h-2zm1-9C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eNota\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eSe pueden agregar tantas reglas como desees. Las siguientes acciones están disponibles en esta grilla:\u003c/div\u003e\n\u003c/div\u003e\n\u003cblockquote\u003e\n\u003cul\u003e\n\u003cli\u003e\n\u003cp\u003eClonar \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/clonar.png\" alt=\"\"\u003e: si una regla ya está definida, al hacer clic en este icono puedes copiar debajo exactamente la misma información.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eOrdenar \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/ordenar%20regla.png\" alt=\"\"\u003e: cuando tengas varias reglas y quieras asignarles un nuevo orden en la grilla, puedes hacer clic en este ícono en el renglón de la regla que deseas mover y arrastrarla hacia su nuevo lugar. La herramienta te notifica de esta posibilidad con un mensaje en la parte inferior de la grilla.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eEliminar \u003cimg src=\"../Documents/Markdown%20Monster%20Weblog%20Posts/eliminar.png\" alt=\"\"\u003e: usa este botón para eliminar una regla.\u003c/p\u003e\n\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/blockquote\u003e\n\u003cdiv class=\"alert alert-success\" role=\"tip\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M9 21c0 .55.45 1 1 1h4c.55 0 1-.45 1-1v-1H9v1zm3-19C8.14 2 5 5.14 5 9c0 2.38 1.19 4.47 3 5.74V17c0 .55.45 1 1 1h6c.55 0 1-.45 1-1v-2.26c1.81-1.27 3-3.36 3-5.74 0-3.86-3.14-7-7-7zm2.85 11.1l-.85.6V16h-4v-2.3l-.85-.6C7.8 12.16 7 10.63 7 9c0-2.76 2.24-5 5-5s5 2.24 5 5c0 1.63-.8 3.16-2.15 4.1z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eConsejo\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eEn la última fila de las reglas puedes agregar un valor que será el que se va a aplicar al resto de los registros. Si ese valor es 'cero' esto significa que la promoción quedará desactivada. Por este motivo, no se debería ubicar la regla con valor cero en la primera fila de la grilla ya que esto generaría que se desactiven los demás criterios.\u003c/div\u003e\n\u003c/div\u003e\n\u003col start=\"12\"\u003e\n\u003cli\u003eUna vez definidos los datos generales de la promoción, los tickets asocidos y las reglas o criterios de aplicación haz clic en \u003cimg src=\"../Desktop/Im%C3%A1genes/Promociones%20comerciales/Guardar.png\" alt=\"\"\u003e.\u003c/li\u003e\n\u003c/ol\u003e\n\u003ch5 id=\"véase-también\"\u003eVéase también\u003c/h5\u003e\n\u003cp\u003eBuscar promociones comerciales\nEditar promociones comerciales\u003c/p\u003e\n"},{id:3,href:"https://bitlogic.github.io/doks-algarrobo/docs/qbit/gestion-enunciados/",title:"Gestionar enunciados",description:"Cuando usas la herramienta puedes ver, editar y agregar preguntas o enunciados vinculados al contenido de una materia. Además, podrás aprender a realizar la validación y aprobación de los enunciados según el rol de las personas que estén a cargo de estas acciones.",content:"\u003cdiv class=\"alert alert-warning\" role=\"alert\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M12 5.99L19.53 19H4.47L12 5.99M12 2L1 21h22L12 2zm1 14h-2v2h2v-2zm0-6h-2v4h2v-4z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eAlerta\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eLa herramienta de gestión de exámenes no permite agregar ni editar información a nivel de programas o contenidos. Estos datos y algunos enunciados relacionados se importan desde otro software o base de datos de la organización educativa. Desde aquí sólo se puede realizar la edición, creación y aprobación de preguntas o enunciados.\u003c/div\u003e\n\u003c/div\u003e\n\u003cp\u003eCuando usas la herramienta puedes ver, editar y agregar preguntas o enunciados vinculados al contenido de una materia. Además, podrás aprender a realizar la validación y aprobación de los enunciados según el rol de las personas que estén a cargo de estas acciones.\u003c/p\u003e\n\u003col\u003e\n\u003cli\u003eAntes de empezar, accede a la herramienta. Haz clic en el ícono de lupa \u003csvg xmlns=\"http://www.w3.org/2000/svg\" width=\"16\" height=\"16\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"currentColor\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\" class=\"feather feather-search\"\u003e\u003ccircle cx=\"11\" cy=\"11\" r=\"8\"\u003e\u003c/circle\u003e\u003cline x1=\"21\" y1=\"21\" x2=\"16.65\" y2=\"16.65\"\u003e\u003c/line\u003e\u003c/svg\u003e ubicado en panel de navegación lateral. Luego ingresa el código o nombre de la materia cuyo contenido deseas ver.\u003c/li\u003e\n\u003cli\u003ePresiona \u003cstrong\u003eBuscar\u003c/strong\u003e. \u003csvg xmlns=\"http://www.w3.org/2000/svg\" width=\"16\" height=\"16\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"currentColor\" stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\" class=\"feather feather-search\"\u003e\u003ccircle cx=\"11\" cy=\"11\" r=\"8\"\u003e\u003c/circle\u003e\u003cline x1=\"21\" y1=\"21\" x2=\"16.65\" y2=\"16.65\"\u003e\u003c/line\u003e\u003c/svg\u003e Los resultados se muestran en la sección “Seleccionar materia”.\u003c/li\u003e\n\u003cli\u003eHaz clic en la materia cuyos enunciados deseas ver o editar. A la derecha se muestran los programas relacionados en sus diferentes estados (activos e inactivos).\u003c/li\u003e\n\u003cli\u003eEn la grilla de resultados, desplázate con la barra inferior hacia la derecha y presiona el ícono de ojo \u003cimg src=\"../../../Downloads/eye.png\" alt=\"\"\u003e para ver los enunciados en aquellos programas que se encuentren activos.\u003c/li\u003e\n\u003cli\u003eEn la pantalla “Gestión de evaluaciones”, usa las flechas para desplegar el contenido de la materia (unidad, tema y subtema).\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"note note-warning\" role=\"note\"\u003e\n  \u003cdiv class=\"flex-shrink-1 note-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0V0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M11 7h2v2h-2zm0 4h2v6h-2zm1-9C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eNota\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003esólo podrán realizar las acciones de editar y crear nuevos enunciados (preguntas) quienes tengan el perfil requerido para tales acciones.\u003c/div\u003e\n\u003c/div\u003e\n\u003col start=\"6\"\u003e\n\u003cli\u003eHaz clic en el icono de signo más \u003cimg src=\"plus.png\" alt=\"\"\u003epara crear un nuevo enunciado. El formulario contiene 5 secciones.\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"alert alert-success\" role=\"tip\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M9 21c0 .55.45 1 1 1h4c.55 0 1-.45 1-1v-1H9v1zm3-19C8.14 2 5 5.14 5 9c0 2.38 1.19 4.47 3 5.74V17c0 .55.45 1 1 1h6c.55 0 1-.45 1-1v-2.26c1.81-1.27 3-3.36 3-5.74 0-3.86-3.14-7-7-7zm2.85 11.1l-.85.6V16h-4v-2.3l-.85-.6C7.8 12.16 7 10.63 7 9c0-2.76 2.24-5 5-5s5 2.24 5 5c0 1.63-.8 3.16-2.15 4.1z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eConsejo\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eAl presionar el icono de lápiz puedes editar la misma información para aquellos enunciados que se hayan guardado anteriormente. Recuerda que si editas un enunciado que ya se encuentra incluído en un examen generado, deberás hacerlo con anticipación. Cuando esto suceda, el sistema te alertará de esta situación.\u003c/div\u003e\n\u003c/div\u003e\n\u003cp\u003eA continuación se detallan la secciones y la información que puede cargar en ellas:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eCaracterísticas: selecciona los valores de los combos desplegables de los campos “Tipo de enunciado” (verdadero/falso o selección múltiple), “Dificultad” (se debe seleccionar alguno de los valores que indican el grado de dificultad), “Tiempo de resolución en minutos” (se incluyen diferentes opciones en minutos según la dificultad), “Para aplicar a”, “Evalúa” (qué tipo de contenido o competencia evalúa la pregunta).\u003c/li\u003e\n\u003cli\u003eEnunciados: en esta sección se puede redactar el enunciado utilizando texto enriquecido, imágenes (formato jpg), links, etc.\u003c/li\u003e\n\u003cli\u003eRespuestas: en esta sección se deben registrar la cantidad de opciones que se quieran proveer al estudiante. En el casillero de selección marca la o las respuesta/s correcta/s.\u003c/li\u003e\n\u003c/ul\u003e\n\u003cdiv class=\"alert alert-success\" role=\"tip\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M9 21c0 .55.45 1 1 1h4c.55 0 1-.45 1-1v-1H9v1zm3-19C8.14 2 5 5.14 5 9c0 2.38 1.19 4.47 3 5.74V17c0 .55.45 1 1 1h6c.55 0 1-.45 1-1v-2.26c1.81-1.27 3-3.36 3-5.74 0-3.86-3.14-7-7-7zm2.85 11.1l-.85.6V16h-4v-2.3l-.85-.6C7.8 12.16 7 10.63 7 9c0-2.76 2.24-5 5-5s5 2.24 5 5c0 1.63-.8 3.16-2.15 4.1z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eConsejo\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eUsa el botón “Agregar opción” y el ícono correspondiente para eliminar la/s acción/es que estén de más. Las respuestas al igual que los enunciados cuentan con la opción de crear texto enriquecido, cargar imágenes, etc.\u003c/div\u003e\n\u003c/div\u003e\n\u003cul\u003e\n\u003cli\u003eJustificación: aquí se debe redactar la justificación de la respuesta correcta.\u003c/li\u003e\n\u003c/ul\u003e\n\u003col start=\"7\"\u003e\n\u003cli\u003ePor último, una vez que hayas ingresado todo el contenido del enunciado presiona el botón \u003cstrong\u003eGuardar\u003c/strong\u003e.\u003c/li\u003e\n\u003cli\u003eUna vez que el enunciado se guarde, se muestra una pantalla de previsualización y validación del enunciado.\u003c/li\u003e\n\u003cli\u003eUna persona con el rol correspondiente podrá seleccionar el estado de la validación y el motivo del cambio de estado.\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"note note-warning\" role=\"note\"\u003e\n  \u003cdiv class=\"flex-shrink-1 note-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0V0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M11 7h2v2h-2zm0 4h2v6h-2zm1-9C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eNota\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eDependiendo de la organización, puede haber un rol o dos asignados a una o más personas a cargo de la revisión técnica y académica.\u003c/div\u003e\n\u003c/div\u003e\n\u003col start=\"10\"\u003e\n\u003cli\u003eUna vez hayas validado los datos, presiona \u003cstrong\u003eGuardar\u003c/strong\u003e.\u003c/li\u003e\n\u003cli\u003eEn la pantalla de contenido del programa podrás ver el nuevo enunciado que se creó.\u003c/li\u003e\n\u003cli\u003eSi el enunciado no se encuentra en estado aprobado y requiere una nueva revisión, selecciónalo y haz clic en el icono de ojo \u003cimg src=\"eye2.png\" alt=\"\"\u003e.\u003c/li\u003e\n\u003cli\u003eLa misma u otra persona con los permisos correspondientes, puede realizar una segunda revisión del enunciado.\u003c/li\u003e\n\u003cli\u003ePor último, guarda los cambios presionando el botón \u003cstrong\u003eGuardar\u003c/strong\u003e.\u003c/li\u003e\n\u003c/ol\u003e\n\u003cdiv class=\"note note-warning\" role=\"note\"\u003e\n  \u003cdiv class=\"flex-shrink-1 note-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0V0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M11 7h2v2h-2zm0 4h2v6h-2zm1-9C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eNota\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eRecuerda que únicamente se encuentran disponibles para incluir en la configuración de tipos de exámenes aquellos enunciados cuyo estado es “Aprobado”.\u003c/div\u003e\n\u003c/div\u003e\n\u003cdiv class=\"alert alert-success\" role=\"tip\"\u003e\n  \u003cdiv class=\"flex-shrink-1 alert-icon\"\u003e\n    \u003csvg xmlns=\"http://www.w3.org/2000/svg\" height=\"20px\" viewBox=\"0 0 24 24\" width=\"20px\" fill=\"#fff\" class=\"icon\"\u003e\n      \u003cpath d=\"M0 0h24v24H0z\" fill=\"none\"/\u003e\n      \u003cpath d=\"M9 21c0 .55.45 1 1 1h4c.55 0 1-.45 1-1v-1H9v1zm3-19C8.14 2 5 5.14 5 9c0 2.38 1.19 4.47 3 5.74V17c0 .55.45 1 1 1h6c.55 0 1-.45 1-1v-2.26c1.81-1.27 3-3.36 3-5.74 0-3.86-3.14-7-7-7zm2.85 11.1l-.85.6V16h-4v-2.3l-.85-.6C7.8 12.16 7 10.63 7 9c0-2.76 2.24-5 5-5s5 2.24 5 5c0 1.63-.8 3.16-2.15 4.1z\"/\u003e\n    \u003c/svg\u003e\n    \u003cspan class=\"icon-title\"\u003eConsejo\u003c/span\u003e\n  \u003c/div\u003e\n  \u003cdiv class=\"w-100\"\u003eEn la grilla con los enunciados relacionados con cada programa también puedes realizar las siguientes acciones mediante los siguientes iconos.\u003c/div\u003e\n\u003c/div\u003e\n\u003cul\u003e\n\u003cli\u003eBuscar \u003cimg src=\"search.png\" alt=\"\"\u003e:  habilita un campo de texto que busca los datos en las diferentes columnas.\u003c/li\u003e\n\u003cli\u003eVer columnas \u003cimg src=\"ver%20columnas%202.png\" alt=\"\"\u003e: se abre un menú contextual con un selector de columnas para elegir qué información ver en la grilla.\u003c/li\u003e\n\u003cli\u003eTabla de filtros \u003cimg src=\"../../../Downloads/tabla%20de%20filtros.png\" alt=\"\"\u003e: sirve para definir ciertos parámetros que acoten la búsqueda. Es una herramienta para una búsqueda avanzada y específica.\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch5 id=\"véase-también\"\u003eVéase también\u003c/h5\u003e\n\u003cp\u003eConfigurar un tipo de examen\u003c/p\u003e\n"},{id:4,href:"https://bitlogic.github.io/doks-algarrobo/docs/prologue/introduction/",title:"Introducción",description:"Descripción funcional del sistema.",content:"\u003ch2 id=\"casos-de-uso\"\u003eCasos de uso\u003c/h2\u003e\n\u003ch3 id=\"gestión-de-cátedras-presenciales\"\u003eGestión de cátedras presenciales\u003c/h3\u003e\n\u003cp\u003eInformación relevante aquí. \u003ca href=\"/doks-algarrobo/docs/prologue/gcp/\"\u003eGCP →\u003c/a\u003e\u003c/p\u003e\n"},{id:5,href:"https://bitlogic.github.io/doks-algarrobo/docs/qbit/abm-tipo-examen/",title:"Configurar un tipo de examen ",description:"El sistema ofrece un asistente para la configuración de los diferentes exámenes que permiten generar la cantidad de instancias de evaluación que desees.",content:"\u003cp\u003eUna vez que los enunciados se encuentran aprobados, puedes comenzar a generar distintos tipos de exámenes que contendrán las preguntas asociadas a las materias.\u003c/p\u003e\n\u003cp\u003eEl sistema ofrece un asistente para la configuración de los diferentes exámenes que permiten generar la cantidad de instancias de evaluación que desees.\u003c/p\u003e\n\u003col\u003e\n\u003cli\u003eEn la pantalla a la que accedes desde la herramienta de gestión de evaluaciones, haz clic en el ícono de lupa \u003cimg src=\"search.png\" alt=\"\"\u003e del panel de navegación lateral e ingresa el código o nombre de la materia cuyo contenido deseas incluir en el tipo de examen.\u003c/li\u003e\n\u003cli\u003ePresiona \u003cstrong\u003eBuscar\u003c/strong\u003e. Los resultados se mostrarán en la sección “Seleccionar materia”.\u003c/li\u003e\n\u003cli\u003eHaz clic en la materia cuyos exámenes quieras configurar. A la derecha verás los programas relacionados en sus diferentes estados (activos e inactivos).\u003c/li\u003e\n\u003cli\u003eEn la grilla de resultados, desplázate con la barra inferior hacia la derecha y presiona el ícono de ojo \u003cimg src=\"eye2.png\" alt=\"\"\u003e para ver los enunciados en aquellos programas que se encuentren activos.\u003c/li\u003e\n\u003cli\u003eEn la pantalla “Gestión de evaluaciones”, haz clic en el ícono de configuración \u003cimg src=\"config.png\" alt=\"\"\u003e ubicado en la parte superior derecha de la pantalla.\u003c/li\u003e\n\u003cli\u003eEn la primera pantalla del asistente de configuración de exámenes, debes seleccionar las unidades, temas y/o subtemas que deseas evaluar. Aquí debes desplegar el contenido de la materia y seleccionar los casillero que correspondan con lo que quieres evaluar.\u003c/li\u003e\n\u003cli\u003eLuego, presiona la flecha \u003cimg src=\"flecha%20asistente.png\" alt=\"\"\u003e![](flecpara pasar a la segunda pantalla del asistente.\u003c/li\u003e\n\u003cli\u003eEn la pantalla número 2, define el nombre del examen, la cantidad de preguntas quieres evaluar, el tipo de generación, y el porcentaje del total de enunciados se van a incluir dependiendo de su configuración previa como preguntas que evalúan competencia o contenido.\u003c/li\u003e\n\u003cli\u003eHaz clic nuevamente en la flecha \u003cimg src=\"flecha%20asistente.png\" alt=\"\"\u003e para continuar.\u003c/li\u003e\n\u003cli\u003eEn la tercera pantalla, configuras si el examen cuenta con control de tiempo e instancia de revisión con sus respectivos tiempos máximos de ejecución. Además, puedes definir si el estudiante tiene la posibilidad de reiniciar el examen (por ejemplo, si corta la conexión a internet o si hay un corte de energía eléctrica mientras se está realizando la evaluación) y cuántas veces puede realizar esta acción. Por último, elige el tipo de cálculo de nota y si ésta se mostrará al finalizar el examen.\nNuevamente avance hacia la última pantalla haciendo clic en la flecha.\u003c/li\u003e\n\u003cli\u003eAvanza al presionar la flecha \u003cimg src=\"flecha%20asistente.png\" alt=\"\"\u003e.\u003c/li\u003e\n\u003cli\u003eLa pantalla de revisión final muestra un resumen de los datos que se configuraron. Si fuese necesario, desde aquí puedes ver si es necesario realizar algunos cambios en las pantallas anteriores o cambiar los estados de algunos enunciados.\u003c/li\u003e\n\u003cli\u003ePara finalizar haga clic en el botón \u003cstrong\u003eGuardar configuración\u003c/strong\u003e.\u003c/li\u003e\n\u003cli\u003eUna vez que se guardaron los cambios en la parte superior del asistente se verá el título del examen acabas de configurar. Si deseas generar un nuevo tipo de examen, presiona nuevamente el signo más \u003cimg src=\"signo%20mas%20asistente.png\" alt=\"\"\u003e en el centro de la pantalla.\u003c/li\u003e\n\u003c/ol\u003e\n\u003ch5 id=\"véase-también\"\u003eVéase también\u003c/h5\u003e\n\u003cp\u003eGestionar enunciados\u003c/p\u003e\n"},{id:6,href:"https://bitlogic.github.io/doks-algarrobo/docs/prologue/gcp/",title:"Gestión de cátedras presenciales",description:"Cuando un usuario con perfil de codocente de planificación ingresa a la herramienta, en la pantalla se mostrará la sección “Gestión de cátedras presenciales”. Aquí, se pueden buscar, ver, editar, y crear cátedras. ",content:"\u003ch2 id=\"menú-lateral\"\u003eMenú lateral\u003c/h2\u003e\n\u003cp\u003eEn el menú ubicado en la barra lateral izquierda se muestran dos indicadores:\u003c/p\u003e\n\u003col\u003e\n\u003cli\u003e\n\u003cp\u003eEl ícono de menú se usa para volver a la pantalla principal.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eEl ícono de almacenamiento en la nube sirve para acceder a la pantalla de “Importación de cátedras”.\u003c/p\u003e\n\u003c/li\u003e\n\u003c/ol\u003e\n\u003ch2 id=\"funcionalidades-adicionales\"\u003eFuncionalidades adicionales\u003c/h2\u003e\n\u003col start=\"3\"\u003e\n\u003cli\u003eEn la parte superior, el formulario de búsqueda de cátedras y seminarios.\u003c/li\u003e\n\u003c/ol\u003e\n\u003ch2 id=\"parte-inferior\"\u003eParte Inferior\u003c/h2\u003e\n\u003col\u003e\n\u003cli\u003e\n\u003cp\u003eResultados de la búsqueda.\u003c/p\u003e\n\u003c/li\u003e\n\u003cli\u003e\n\u003cp\u003eA la derecha, con el ícono de signo más podrá realizar más acciones como:\u003c/p\u003e\n\u003c/li\u003e\n\u003c/ol\u003e\n\u003cul\u003e\n\u003cli\u003ecrear una nueva cátedra presencial,\u003c/li\u003e\n\u003cli\u003ecrear una cátedra virtual,\u003c/li\u003e\n\u003cli\u003ecrear un seminario \u003cstrong\u003eExperimenta\u003c/strong\u003e, o\u003c/li\u003e\n\u003cli\u003ecrear un seminario común.\u003c/li\u003e\n\u003c/ul\u003e\n\u003cimg class=\"img-simple img-fluid lazyload blur-up\" src=\"https://bitlogic.github.io/doks-algarrobo/docs/prologue/gcp/gcp_hu69c5807cf54752dd05b9c7e718740961_113158_20x0_resize_box_2.png\" data-src=\"https://bitlogic.github.io/doks-algarrobo/docs/prologue/gcp/gcp.png\" width=\"1453\" height=\"683\" alt=\"Gestión de cátedras presenciales\"\u003e\n"},{id:7,href:"https://bitlogic.github.io/doks-algarrobo/docs/prologue/",title:"Prologue",description:"Prologue.",content:""},{id:8,href:"https://bitlogic.github.io/doks-algarrobo/docs/",title:"Docs",description:"Software project docs",content:""},{id:9,href:"https://bitlogic.github.io/doks-algarrobo/docs/qbit/",title:"QBit",description:"QBit docs",content:""},];index.add(docs);userinput.addEventListener('input',show_results,true);suggestions.addEventListener('click',accept_suggestion,true);function show_results(){var value=this.value;var results=index.search(value,5);var entry,childs=suggestions.childNodes;var i=0,len=results.length;suggestions.classList.remove('d-none');results.forEach(function(page){entry=document.createElement('div');entry.innerHTML='<a href><span></span><span></span></a>';a=entry.querySelector('a'),t=entry.querySelector('span:first-child'),d=entry.querySelector('span:nth-child(2)');a.href=page.href;t.textContent=page.title;d.textContent=page.description;suggestions.appendChild(entry);});while(childs.length>len){suggestions.removeChild(childs[i])}}
function accept_suggestion(){while(suggestions.lastChild){suggestions.removeChild(suggestions.lastChild);}
return false;}}());